import React, { useState, useEffect } from 'react';
import { getInvoices, getBulkData, handleApiError, formatCurrency, formatDate, getInvoiceStatus } from '../utils/api';

const InvoicePanel = ({ selectedInvoice, refreshTrigger, onInvoiceSelect }) => {
  const [invoices, setInvoices] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [view, setView] = useState('list');

  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        setError(null);

        const [invoicesData, bulkData] = await Promise.all([
          getInvoices({ limit: 50 }),
          getBulkData()
        ]);

        const invoiceList = invoicesData.QueryResponse?.Invoice || [];
        setInvoices(invoiceList);

        const totalRevenue = invoiceList.reduce((sum, inv) => sum + parseFloat(inv.TotalAmt || 0), 0);
        const unpaidInvoices = invoiceList.filter(inv => parseFloat(inv.Balance || 0) > 0);
        const totalUnpaid = unpaidInvoices.reduce((sum, inv) => sum + parseFloat(inv.Balance || 0), 0);

        setAnalytics({
          totalInvoices: invoiceList.length,
          totalRevenue,
          paidRevenue: totalRevenue - totalUnpaid,
          unpaidCount: unpaidInvoices.length,
          unpaidAmount: totalUnpaid
        });

      } catch (err) {
        setError(handleApiError(err));
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, [refreshTrigger]);

  useEffect(() => {
    if (selectedInvoice) {
      setView('detail');
    }
  }, [selectedInvoice]);

  const renderStatusBadge = (invoice) => {
    const status = getInvoiceStatus(invoice);
    const badgeClasses = {
      paid: 'status-badge status-paid',
      unpaid: 'status-badge status-unpaid',
      overdue: 'status-badge status-overdue'
    };

    return <span className={badgeClasses[status]}>{status.charAt(0).toUpperCase() + status.slice(1)}</span>;
  };

  if (isLoading) {
    return (
      <div className="h-full flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500 mx-auto mb-2"></div>
          <p className="text-sm text-gray-600">Loading invoice data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="h-full flex items-center justify-center p-4">
        <div className="text-center">
          <h3 className="text-lg font-medium text-gray-900 mb-2">Error Loading Data</h3>
          <p className="text-sm text-gray-600 mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="px-3 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="flex-shrink-0 p-4 border-b border-gray-200 bg-white">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-gray-900">Invoice Data</h2>
          <div className="flex space-x-1">
            <button
              onClick={() => setView('list')}
              className={`px-3 py-1 text-sm font-medium rounded-md ${
                view === 'list' ? 'bg-primary-100 text-primary-700' : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              List
            </button>
            <button
              onClick={() => setView('analytics')}
              className={`px-3 py-1 text-sm font-medium rounded-md ${
                view === 'analytics' ? 'bg-primary-100 text-primary-700' : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              Analytics
            </button>
            {selectedInvoice && (
              <button
                onClick={() => setView('detail')}
                className={`px-3 py-1 text-sm font-medium rounded-md ${
                  view === 'detail' ? 'bg-primary-100 text-primary-700' : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                Detail
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4 scrollbar-thin">
        {/* Invoice List View */}
        {view === 'list' && (
          <div className="space-y-3">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">Recent Invoices</h3>
              <span className="text-sm text-gray-500">{invoices.length} invoices</span>
            </div>
            
            {invoices.length === 0 ? (
              <div className="text-center py-8">
                <h3 className="text-sm font-medium text-gray-900">No invoices found</h3>
                <p className="text-sm text-gray-500">Ask the AI to show your invoices.</p>
              </div>
            ) : (
              <div className="space-y-2 max-h-96 overflow-y-auto scrollbar-thin">
                {invoices.map((invoice) => (
                  <div
                    key={invoice.Id}
                    onClick={() => onInvoiceSelect(invoice)}
                    className="invoice-card cursor-pointer"
                  >
                    <div className="flex items-center justify-between mb-1">
                      <h4 className="text-sm font-medium text-gray-900">Invoice #{invoice.DocNumber}</h4>
                      {renderStatusBadge(invoice)}
                    </div>
                    <p className="text-sm text-gray-600">{invoice.CustomerRef?.name || 'Unknown Customer'}</p>
                    <div className="flex items-center justify-between mt-2">
                      <span className="text-lg font-semibold text-gray-900">{formatCurrency(invoice.TotalAmt)}</span>
                      <span className="text-sm text-gray-500">{formatDate(invoice.TxnDate)}</span>
                    </div>
                    {parseFloat(invoice.Balance || 0) > 0 && (
                      <div className="mt-1">
                        <span className="text-xs text-red-600">Balance: {formatCurrency(invoice.Balance)}</span>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Analytics View */}
        {view === 'analytics' && analytics && (
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Business Analytics</h3>
            
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-primary-50 border border-primary-200 rounded-lg p-4">
                <p className="text-sm font-medium text-primary-600">Total Revenue</p>
                <p className="text-2xl font-semibold text-primary-900">{formatCurrency(analytics.totalRevenue)}</p>
              </div>
              <div className="bg-success-50 border border-success-200 rounded-lg p-4">
                <p className="text-sm font-medium text-success-600">Paid Revenue</p>
                <p className="text-2xl font-semibold text-success-900">{formatCurrency(analytics.paidRevenue)}</p>
              </div>
              <div className="bg-warning-50 border border-warning-200 rounded-lg p-4">
                <p className="text-sm font-medium text-warning-600">Unpaid ({analytics.unpaidCount})</p>
                <p className="text-2xl font-semibold text-warning-900">{formatCurrency(analytics.unpaidAmount)}</p>
              </div>
              <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
                <p className="text-sm font-medium text-gray-600">Total Invoices</p>
                <p className="text-2xl font-semibold text-gray-900">{analytics.totalInvoices}</p>
              </div>
            </div>
          </div>
        )}

        {/* Detail View */}
        {view === 'detail' && (
          <div className="space-y-4">
            {!selectedInvoice ? (
              <div className="text-center py-8">
                <h3 className="text-sm font-medium text-gray-900">No invoice selected</h3>
                <p className="text-sm text-gray-500">Click on an invoice or ask the AI to show details.</p>
              </div>
            ) : (
              <>
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-900">Invoice Details</h3>
                  <button
                    onClick={() => setView('list')}
                    className="text-sm text-primary-600 hover:text-primary-700"
                  >
                    ‚Üê Back to List
                  </button>
                </div>

                <div className="bg-white border border-gray-200 rounded-lg p-6">
                  <div className="flex items-center justify-between mb-4">
                    <div>
                      <h4 className="text-xl font-semibold text-gray-900">
                        Invoice #{selectedInvoice.DocNumber || selectedInvoice.number}
                      </h4>
                      <p className="text-sm text-gray-600">
                        {selectedInvoice.CustomerRef?.name || selectedInvoice.customer?.name || selectedInvoice.customer}
                      </p>
                    </div>
                    {renderStatusBadge(selectedInvoice)}
                  </div>

                  <div className="grid grid-cols-2 gap-6">
                    <div>
                      <h5 className="text-sm font-medium text-gray-700 mb-2">Invoice Information</h5>
                      <div className="space-y-1 text-sm">
                        <div className="flex justify-between">
                          <span className="text-gray-600">Date:</span>
                          <span>{formatDate(selectedInvoice.TxnDate || selectedInvoice.date)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">Due Date:</span>
                          <span>{formatDate(selectedInvoice.DueDate || selectedInvoice.dueDate)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">Amount:</span>
                          <span className="font-medium">{formatCurrency(selectedInvoice.TotalAmt || selectedInvoice.amount)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">Balance:</span>
                          <span className="font-medium">{formatCurrency(selectedInvoice.Balance || selectedInvoice.balance)}</span>
                        </div>
                      </div>
                    </div>
                    <div>
                      <h5 className="text-sm font-medium text-gray-700 mb-2">Customer</h5>
                      <p className="text-sm">{selectedInvoice.CustomerRef?.name || selectedInvoice.customer?.name || selectedInvoice.customer}</p>
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default InvoicePanel;
